import React, { useEffect } from 'react'
import '../App.css'
import { Button } from 'react-bootstrap';
import { useState } from 'react';
import db from 'firebase';

const Field = () => {

    const [inputData, setinputData] = useState(' ');
    const [items, setItems] = useState([]);

    useEffect (() => {
        db.collection('List').onSnapshot(snapshot => {
            
        })
    })

    const addItem = () => {
        if (!inputData) {

        } else {
            setItems([...items, inputData]);
            setinputData('')
        }

    }

    const deleteItem = (id) => {
        const updateditems = items.filter((elem, ind) => {
            return ind !== id;
        })
        setItems(updateditems);
    }
    return (
        <div className='Main'>

            <div className='ShowItem'>
                {
                    items.map((elem, ind) => {
                        return (
                            <div className='EachItem' key={ind}>
                                {elem}
                                <i className='Del' class="fa-solid fa-xmark" onClick={() => deleteItem(ind)}></i>
                            </div>

                        )

                    })
                }

            </div>
            <br />

            <div className='Child'>
                <form>
                    <input className='Input' type='text' placeholder='Type Here'
                        value={inputData}
                        onChange={(e) => setinputData(e.target.value)} />
                    <Button className='Button' onClick={addItem}>Add</Button>
                    </form>
            </div>
        </div>

    )
}

export default Field
